arr(); // 1

function foo(n) {
  var f = () => arguments[0] + n; // foo's implicit arguments binding. arguments[0] is n
  return f();
}

foo(3); // 3 + 3 = 6

function foo(n) {
  var f = (...args) => args[0] + n;
  return f(10);
}

foo(1); // 11

