const StyledTumbleWeed = styled(TumbleWeedSvg)`
  animation: tumbleweed linear 5s infinite;
  background: transparent !important;
  height: 5em;
  position: absolute;
  bottom: 0;
  animation-play-state: ${props => (props.paused ? `paused` : `running`)};
  @keyframes tumbleweed {
    0% {
      transform: translate(-30vw, -100%) rotate(0deg);
    }
    10% {
      transform: translate(-20vw, -120%) rotate(130deg);
    }
    20% {
      transform: translate(0, -100%) rotate(250deg);
    }
    30% {
      transform: translate(10vw, -120%) rotate(360deg);
    }
    40% {
      transform: translate(40vw, -100%) rotate(490deg);
    }
    50% {
      transform: translate(50vw, -120%) rotate(600deg);
    }
    60% {
      transform: translate(60vw, -100%) rotate(720deg);
    }
    100% {
      transform: translate(105vw, -120%) rotate(1000deg);
    }
  }
`

export const TumbleWeed = props => {
  const [paused, setPaused] = useState(false)
  return (
    <StyledTumbleWeed
      {...props}
      paused={paused}
      onClick={() => setPaused(!paused)}
    />
  )
}
